package main.java.com.acme.mytrader.execution;/* Monitors price movements on a specified single stock IBM Executes a single "buy" for number of lots 100*/public class ExecutionServiceImpl implements ExecutionService {    private static final String SECURITY = "KEY_123";    private static final String STOCK = "IBM";    @Override    public void buy(String security, double price, int volume) {        if(SECURITY.equals(security)){            System.out.println(String.format("Stock %s of %s units for amount %s has been bought successfully", STOCK, volume, (volume*price) ));        }    }    @Override    public void sell(String security, double price, int volume) {    }}