package main.java.com.acme.mytrader.price;import main.java.com.acme.mytrader.execution.ExecutionService;/* Given  number of lots is 100 and lower price limit is 55.0 and given stock "IBM". * Checking condition if price less than lowerPriceLimit or not.  * If less than lowerPriceLimit than purchase the stock*/public class PriceListenerImpl implements PriceListener {    private static final int units = 100;    private static final double lowerPriceLimit = 55.00;    private ExecutionService executionService;    public PriceListenerImpl(ExecutionService executionService){        this.executionService = executionService;    }    @Override    public void priceUpdate(String security, double price) {        if(price <= lowerPriceLimit){            executionService.buy(security, price, units);        }    }}