package main.java.com.acme.mytrader.price;import java.util.ArrayList;import java.util.List;/* PriceSource implementation methods contains List of priceListeners and setting price to priceListeners in iteration */public class PriceSourceImpl implements PriceSource {    private double price;    List<PriceListener> priceListeners = new ArrayList<>();    @Override    public void addPriceListener(PriceListener listener) {        priceListeners.add(listener);    }    @Override    public void removePriceListener(PriceListener listener) {        priceListeners.add(listener);    }    public double getPrice() {        return price;    }    public void setPrice(double price) {        this.price = price;        priceListeners.forEach(priceListener -> priceListener.priceUpdate("KEY_123", price));    }}